"use strict";
$(document).ready(function () {
  var t = $(".templatemo-accordion > li > ul").hide();
  $(".templatemo-accordion > li > a").click(function () {
    var a = $(this).next();
    return (
      a.hasClass("active") ||
        (t.removeClass("active").slideUp(), a.addClass("active").slideDown()),
      !1
    );
  }),
    $(".product-links-wap a").click(function () {
      var this_src = $(this).children('img').attr('src');
      let sub_src = this_src.slice(0,this_src.indexOf("eyecatch"))
      let eyecatch_len = "eyecatch".length
      let sub_src2 = this_src.slice(this_src.indexOf("eyecatch")+eyecatch_len)
      this_src = sub_src + "on_preview" + sub_src2
      return $('#product-detail').attr('src',this_src), !1;
    }),
    $("#btn-minus").click(function () {
      var t = $("#var-value").html();
      return (
        (t = "1" == t ? t : t - 1),
        $("#var-value").html(t),
        $("#product-quanity").val(t),
        !1
      );
    }),
    $("#btn-plus").click(function () {
      var t = $("#var-value").html();
      return t++, $("#var-value").html(t), $("#product-quanity").val(t), !1;
    }),
    $(".btn-size").click(function () {
      var t = $(this).html();
      return (
        $("#product-size").val(t),
        $(".btn-size").removeClass("btn-secondary"),
        $(".btn-size").addClass("btn-success"),
        $(this).removeClass("btn-success"),
        $(this).addClass("btn-secondary"),
        !1
      );
    });
    $(".btn-color").click(function () {
      let t = "";
      if ($(".btn-color").hasClass("bg-secondary")) {
        t = "";
        return (
          $("#product-color").val(t),
          $(".btn-color").removeClass("bg-secondary"),
          $(".btn-color").addClass("bg-success"),
          !1
          )
        }
        else if ($(".btn-color").hasClass("bg-success")) {
        t = $(this).html();
        return (
          $("#product-color").val(t),
          $(this).removeClass("bg-success"),
          $(this).addClass("bg-secondary"),
          !1
        );
      }
    });
});
